import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./App.css";

function App() {
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState('');
  const [conversation, setConversation] = useState([]);
  const [sessionId, setSessionId] = useState(null); // For managing session ID

  // Generate a unique session ID when the component mounts
  useEffect(() => {
    const generateSessionId = () => {
      return Math.random().toString(36).substr(2, 9); // Generates a random session ID
    };

    const existingSessionId = localStorage.getItem('sessionId');
    if (existingSessionId) {
      setSessionId(existingSessionId);
    } else {
      const newSessionId = generateSessionId();
      localStorage.setItem('sessionId', newSessionId);
      setSessionId(newSessionId);
    }
  }, []);

  const handleSend = async () => {
    if (question.trim() && sessionId) {
      try {
        // Send question and session_id to FastAPI backend
        const res = await axios.post("http://localhost:8000/ask", {
          question,
          session_id: sessionId,  // Send session ID along with the question
        });
        const botResponse = res.data.response;

        // Update conversation history
        const newConversation = [...conversation, { question, botResponse }];
        setConversation(newConversation);

        setResponse(botResponse);
        setQuestion(''); // Clear input field after sending
      } catch (error) {
        console.error('Error fetching response:', error);
        setResponse('Error fetching response.');
      }
    }
  };

  return (
    <div className="App">
      <h1>Insurance Agent Chatbot</h1>
      <div className="chat-box">
        {conversation.map((msg, index) => (
          <div key={index}>
            <p><strong>User:</strong> {msg.question}</p>
            <p><strong>Bot:</strong> {msg.botResponse}</p>
          </div>
        ))}
      </div>

      <input
        type="text"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        placeholder="Ask me something..."
      />
      <button className='btn' onClick={handleSend}>Send</button>

      {response && <p><strong>Bot Response:</strong> {response}</p>}
    </div>
  );
}

export default App;
